import java.io.*;
import java.util.Scanner;
public class M08計算兩數和的平方 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.print("請輸入數字:");
		Scanner sc = new Scanner(System.in);
		int a = sc.nextInt();
		System.out.print("請輸入數字:");
		int b = sc.nextInt();
		System.out.print("相加平方答案:"+ (a+b)*(a+b));;

	}

}
